      console.log(`üîå Conectando ao WebSocket: ${wsUrl}`);

      const ws = new WebSocket(wsUrl);
      wsRef.current = ws;

      ws.onopen = () => {
        console.log('‚úÖ WebSocket conectado');
        setIsConnected(true);
        setError(null);
        reconnectAttemptsRef.current = 0;

        // Limpar timeout de reconex√£o
        if (reconnectTimeoutRef.current) {
          clearTimeout(reconnectTimeoutRef.current);
        }

        toast.success('Sincroniza√ß√£o em tempo real ativada', {
          description: 'Mudan√ßas ser√£o detectadas instantaneamente'
        });
      };

      ws.onmessage = async (event) => {
        try {
          const message = JSON.parse(event.data);
          console.log('üì® Mensagem recebida:', message.type);

          if (message.type === 'initial' || message.type === 'sync') {
            await processSyncMessage(message.data);
          } else if (message.type === 'pong') {
            console.log('üèì Pong recebido');
          }

        } catch (error) {
          console.error('Erro ao processar mensagem:', error);
        }
      };

      ws.onerror = (event) => {
        console.error('‚ùå Erro no WebSocket:', event);
        const err = new Error('Erro na conex√£o WebSocket');
        setError(err);
        onError?.(err);
      };

      ws.onclose = () => {
        console.log('üîå WebSocket desconectado');
        setIsConnected(false);
        wsRef.current = null;

        // Tentar reconectar se ainda estiver habilitado
        if (enabled && reconnectAttemptsRef.current < maxReconnectAttempts) {
          reconnectAttemptsRef.current++;
          console.log(`üîÑ Tentando reconectar (${reconnectAttemptsRef.current}/${maxReconnectAttempts})...`);

          reconnectTimeoutRef.current = setTimeout(() => {
            connect();
          }, reconnectInterval);
        } else if (reconnectAttemptsRef.current >= maxReconnectAttempts) {
          toast.error('N√£o foi poss√≠vel conectar ao servidor de sincroniza√ß√£o', {
            description: 'Verifique se o servidor est√° rodando'
          });
        }
      };

    } catch (error) {
      console.error('Erro ao criar WebSocket:', error);
      setError(error as Error);
      onError?.(error as Error);
    }
  }, [enabled, wsUrl, reconnectInterval, maxReconnectAttempts, processSyncMessage, onError]);

  // Desconectar WebSocket
  const disconnect = useCallback(() => {
    if (wsRef.current) {
      wsRef.current.close();
      wsRef.current = null;
    }

    if (reconnectTimeoutRef.current) {
      clearTimeout(reconnectTimeoutRef.current);
    }

    setIsConnected(false);
  }, []);

  // Enviar ping para manter conex√£o viva
  const sendPing = useCallback(() => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({
        type: 'ping',
        timestamp: new Date().toISOString()
      }));
    }
  }, []);

  // Solicitar sincroniza√ß√£o manual
  const requestSync = useCallback(() => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({
        type: 'request_sync',
        timestamp: new Date().toISOString()
      }));

      toast.info('Solicitando sincroniza√ß√£o...');
    } else {
      toast.error('WebSocket n√£o est√° conectado');
    }
  }, []);

  // Efeito para conectar/desconectar
  useEffect(() => {
    if (enabled) {
      connect();
    } else {
      disconnect();
    }

    return () => {
      disconnect();
    };
  }, [enabled, connect, disconnect]);

  // Efeito para manter conex√£o viva com ping
  useEffect(() => {
    if (!isConnected) return;

    const pingInterval = setInterval(sendPing, 30000); // Ping a cada 30s

    return () => {
      clearInterval(pingInterval);
    };
  }, [isConnected, sendPing]);

  return {
    isConnected,
    lastSync,
    syncCount,
    error,
    requestSync,
    disconnect,
    reconnect: connect
  };
}

