}

interface Document {
  id: string
  title: string
  content: string
  type: string
  source: string
  metadata?: any
  created_at?: string
  score?: number
}

interface Stats {
  total_documents: number
  types: Record<string, number>
  cache_size_bytes: number
  index_size_bytes: number
  vectors_size_bytes: number
  total_size_bytes: number
  version: string
  features: {
    vector_search: boolean
    validation: boolean
    auto_indexing: boolean
    tf_idf: boolean
  }
}

