{
  "name": "offline-first-rag-evaluator",
  "version": "1.0.0",
  "description": "AI agent specialized in evaluating and optimizing RAG systems for offline-first functionality",
  "protocol": "a2a",
  "protocolVersion": "0.2.5",
  "url": "http://localhost:8800",
  "endpoints": {
    "agent_card": "/.well-known/agent-card",
    "tasks": "/a2a/tasks",
    "messages": "/a2a/tasks/{taskId}/messages",
    "status": "/a2a/tasks/{taskId}/status",
    "stream": "/a2a/tasks/{taskId}/stream"
  },
  "skills": [
    {
      "name": "evaluate_rag_system",
      "description": "Comprehensive evaluation of RAG system architecture for offline-first capabilities",
      "parameters": {
        "type": "object",
        "properties": {
          "target_system": {
            "type": "string",
            "description": "Path or identifier of the RAG system to evaluate"
          },
          "evaluation_depth": {
            "type": "string",
            "enum": ["basic", "comprehensive", "deep"],
            "default": "comprehensive"
          },
          "focus_areas": {
            "type": "array",
            "items": {
              "type": "string",
              "enum": ["cache", "sync", "performance", "ux", "resilience"]
            }
          }
        },
        "required": ["target_system"]
      },
      "returns": {
        "type": "object",
        "properties": {
          "score": {
            "type": "number",
            "minimum": 0,
            "maximum": 100
          },
          "report": {
            "type": "string",
            "format": "markdown"
          },
          "recommendations": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "priority": {
                  "type": "string",
                  "enum": ["high", "medium", "low"]
                },
                "title": {
                  "type": "string"
                },
                "description": {
                  "type": "string"
                },
                "implementation": {
                  "type": "string"
                }
              }
            }
          }
        }
      }
    },
    {
      "name": "test_offline_functionality",
      "description": "Test specific RAG features in offline mode",
      "parameters": {
        "type": "object",
        "properties": {
          "feature": {
            "type": "string",
            "description": "Feature to test offline"
          },
          "test_scenarios": {
            "type": "array",
            "items": {
              "type": "string"
            }
          }
        },
        "required": ["feature"]
      },
      "returns": {
        "type": "object",
        "properties": {
          "passed": {
            "type": "boolean"
          },
          "results": {
            "type": "array"
          },
          "issues": {
            "type": "array"
          }
        }
      }
    },
    {
      "name": "optimize_cache_strategy",
      "description": "Analyze and optimize caching strategies for offline performance",
      "parameters": {
        "type": "object",
        "properties": {
          "current_strategy": {
            "type": "object",
            "description": "Current caching configuration"
          },
          "constraints": {
            "type": "object",
            "properties": {
              "max_cache_size": {
                "type": "number"
              },
              "target_hit_rate": {
                "type": "number"
              }
            }
          }
        }
      },
      "returns": {
        "type": "object",
        "properties": {
          "optimized_strategy": {
            "type": "object"
          },
          "expected_improvements": {
            "type": "object"
          },
          "implementation_code": {
            "type": "string"
          }
        }
      }
    }
  ],
  "capabilities": {
    "max_concurrent_tasks": 5,
    "supported_content_types": ["text/plain", "application/json", "text/markdown"],
    "max_message_size": 1048576,
    "streaming_support": true,
    "push_notification_support": false,
    "offline_mode": true
  },
  "metadata": {
    "author": "Claude Assistant",
    "license": "MIT",
    "documentation": "https://github.com/anthropics/offline-first-rag-evaluator",
    "tags": ["rag", "offline-first", "evaluation", "optimization", "a2a-protocol"],
    "category": "evaluation-tools"
  }
}

