from functools import lru_cache

@agent(name="Optimized Extraction Agent")
class OptimizedExtractionAgent(A2AServer):
    def __init__(self):
        super().__init__()
        self.results_cache = {}

    @lru_cache(maxsize=1000)
    def expensive_extraction(self, input_text):
        """Cache para evitar reprocessamento"""
        # LÃ³gica de extraÃ§Ã£o pesada
        return self.process_complex_extraction(input_text)

    def handle_task(self, task):
        # Verificar cache primeiro
        cache_key = str(task.input)
        if cache_key in self.results_cache:
            cached_result = self.results_cache[cache_key]
            return self.create_cached_response(cached_result)

        # Processar nova extraÃ§Ã£o
        result = self.expensive_extraction(cache_key)
        self.results_cache[cache_key] = result

