    """

    def __init__(self):
        self.rag = SimpleRAG()
        self.cache = {}

    async def process_query(self, query: str) -> Dict[str, Any]:
        """
        Processa uma query usando o RAG
        """
        # Verificar cache
        if query in self.cache:
