# Variables
PYTHON := python3
PIP := pip
VENV := venv
PORT := 5001

# Colors for output
BLUE := \033[0;34m
GREEN := \033[0;32m
YELLOW := \033[0;33m
RED := \033[0;31m
NC := \033[0m # No Color

help: ## Show this help message
	@echo "$(BLUE)MCP RAG Server - Available Commands$(NC)"
	@echo ""
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | sort | awk 'BEGIN {FS = ":.*?## "}; {printf "$(GREEN)%-15s$(NC) %s\n", $$1, $$2}'

install: ## Install all dependencies
	@echo "$(YELLOW)Installing dependencies...$(NC)"
	@if [ ! -d "$(VENV)" ]; then \
		$(PYTHON) -m venv $(VENV); \
	fi
	@. $(VENV)/bin/activate && $(PIP) install --upgrade pip
	@. $(VENV)/bin/activate && $(PIP) install -r requirements.txt
	@echo "$(GREEN)‚úì Dependencies installed$(NC)"

setup: install ## Complete setup (venv + dependencies)
	@echo "$(GREEN)‚úì Setup complete! Activate with: source $(VENV)/bin/activate$(NC)"

test: ## Run tests
	@echo "$(YELLOW)Running tests...$(NC)"
	@. $(VENV)/bin/activate && $(PYTHON) test_improved.py
	@echo "$(GREEN)‚úì Tests completed$(NC)"

lint: ## Lint Python code with ruff
	@echo "$(YELLOW)Linting code...$(NC)"
	@. $(VENV)/bin/activate && ($(PIP) show ruff > /dev/null 2>&1 || $(PIP) install ruff)
	@. $(VENV)/bin/activate && ruff check *.py
	@echo "$(GREEN)‚úì Linting completed$(NC)"

format: ## Format Python code with black
	@echo "$(YELLOW)Formatting code...$(NC)"
	@. $(VENV)/bin/activate && ($(PIP) show black > /dev/null 2>&1 || $(PIP) install black)
	@. $(VENV)/bin/activate && black *.py
	@echo "$(GREEN)‚úì Formatting completed$(NC)"

start-mcp: ## Start MCP RAG server
