import json
import sys
import os
from pathlib import Path

# Cache path
CACHE_PATH = Path.home() / ".claude" / "mcp-rag-cache"
CACHE_FILE = CACHE_PATH / "documents.json"

class RAGServer:
    def __init__(self):
        self.documents = []
        self.load_documents()

    def load_documents(self):
        """Carrega documentos do cache"""
        if CACHE_FILE.exists():
            try:
                with open(CACHE_FILE, 'r', encoding='utf-8') as f:
                    data = json.load(f)
                    self.documents = data.get('documents', [])
            except:
                self.documents = []

    def save_documents(self):
        """Salva documentos no cache"""
        CACHE_PATH.mkdir(parents=True, exist_ok=True)
        with open(CACHE_FILE, 'w', encoding='utf-8') as f:
            json.dump({'documents': self.documents}, f, ensure_ascii=False, indent=2)

    def search(self, query, limit=5):
        """Busca simples por texto"""
        query_lower = query.lower()
        results = []

        for doc in self.documents:
            content = f"{doc.get('title', '')} {doc.get('content', '')}".lower()
            if query_lower in content:
                results.append({
                    'id': doc.get('id'),
                    'title': doc.get('title'),
                    'content': doc.get('content', '')[:200] + '...' if len(doc.get('content', '')) > 200 else doc.get('content', ''),
                    'type': doc.get('type'),
                    'source': doc.get('source')
                })

                if len(results) >= limit:
                    break

        return results

    def add_document(self, doc):
        """Adiciona documento"""
        if 'id' not in doc:
            import time
            doc['id'] = f"doc_{int(time.time() * 1000)}"

        self.documents.append(doc)
        self.save_documents()
        return doc

    def remove_document(self, doc_id):
        """Remove documento"""
        for i, doc in enumerate(self.documents):
            if doc.get('id') == doc_id:
                self.documents.pop(i)
                self.save_documents()
                return True
        return False

    def list_documents(self):
        """Lista todos os documentos"""
        return [{
            'id': doc.get('id'),
            'title': doc.get('title'),
            'type': doc.get('type'),
            'source': doc.get('source')
        } for doc in self.documents]

    def get_stats(self):
        """Estatísticas do cache"""
        return {
            'total_documents': len(self.documents),
            'cache_file': str(CACHE_FILE),
            'cache_size_bytes': CACHE_FILE.stat().st_size if CACHE_FILE.exists() else 0
        }

