import React, { useState, useEffect } from 'react'
import { Badge } from '@/components/ui/badge'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog'
import { Textarea } from '@/components/ui/textarea'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { useToast } from '@/components/ui/use-toast'
import { ragAPI, type RAGDocument, type RAGStats } from '@/services/rag-api'
import { Search, Plus, Tag, FolderOpen, Hash, Brain, FileText, Trash2, Edit } from 'lucide-react'

export function RAGManagerV2() {
  const [documents, setDocuments] = useState<RAGDocument[]>([])
  const [stats, setStats] = useState<RAGStats | null>(null)
  const [loading, setLoading] = useState(true)
  const [searchQuery, setSearchQuery] = useState('')
  const [searchResults, setSearchResults] = useState<RAGDocument[]>([])
  const [selectedDoc, setSelectedDoc] = useState<RAGDocument | null>(null)
  const [searchType, setSearchType] = useState<'semantic' | 'text'>('semantic')
  const [selectedCategory, setSelectedCategory] = useState<string>('all')
  const [selectedTags, setSelectedTags] = useState<string[]>([])
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false)
  const [newDoc, setNewDoc] = useState({
    title: '',
    content: '',
    tags: '',
    category: 'uncategorized',
    source: 'manual'
  })
  const { toast } = useToast()

  // Carregar documentos e estatísticas
  const loadData = async () => {
    try {
      setLoading(true)
      const { documents: docs, stats: statsData } = await ragAPI.loadLocalCache()
      setDocuments(docs)
      setStats(statsData)
    } catch (error) {
      console.error('Erro ao carregar dados:', error)
      toast({
        title: 'Erro',
        description: 'Não foi possível carregar os documentos',
        variant: 'destructive'
      })
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    loadData()
  }, [])

  // Busca semântica ou textual
  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setSearchResults([])
      return
    }

    try {
      const result = await ragAPI.search(searchQuery, 10, searchType === 'semantic')
      setSearchResults(result.results)

      toast({
        title: 'Busca realizada',
        description: `${result.total} resultados encontrados (${result.search_type})`,
      })
    } catch (error) {
      // Fallback para busca local
      const filtered = documents.filter(doc => {
        const searchContent = `${doc.title} ${doc.content} ${doc.tags?.join(' ')}`.toLowerCase()
        return searchContent.includes(searchQuery.toLowerCase())
      })
      setSearchResults(filtered)
    }
  }

  // Busca por categoria
  const handleCategoryFilter = async (category: string) => {
    setSelectedCategory(category)

    if (category === 'all') {
      setSearchResults([])
      return
    }

    try {
      const result = await ragAPI.searchByCategory(category)
      setSearchResults(result.results)
    } catch (error) {
      // Fallback local
      const filtered = documents.filter(doc => doc.category === category)
      setSearchResults(filtered)
    }
  }

  // Busca por tags
  const handleTagFilter = async (tag: string) => {
    const newTags = selectedTags.includes(tag)
      ? selectedTags.filter(t => t !== tag)
      : [...selectedTags, tag]

    setSelectedTags(newTags)

    if (newTags.length === 0) {
      setSearchResults([])
      return
    }

    try {
      const result = await ragAPI.searchByTags(newTags)
      setSearchResults(result.results)
    } catch (error) {
      // Fallback local
      const filtered = documents.filter(doc => 
        doc.tags?.some(t => newTags.includes(t))
      )
      setSearchResults(filtered)
    }
  }

  // Adicionar novo documento
  const handleAddDocument = async () => {
    try {
      const doc = await ragAPI.addDocument({
        title: newDoc.title,
        content: newDoc.content,
        tags: newDoc.tags.split(',').map(t => t.trim()).filter(t => t),
        category: newDoc.category,
        source: newDoc.source,
        type: 'text'
      })

      setDocuments([...documents, doc])
      setIsAddDialogOpen(false)
      setNewDoc({
        title: '',
        content: '',
        tags: '',
        category: 'uncategorized',
        source: 'manual'
      })

      toast({
        title: 'Documento adicionado',
        description: `${doc.title} foi adicionado com sucesso`,
      })

      loadData() // Recarregar dados
    } catch (error) {
      toast({
        title: 'Erro',
        description: 'Não foi possível adicionar o documento',
        variant: 'destructive'
      })
    }
  }

  // Remover documento
  const handleRemoveDocument = async (id: string) => {
    try {
      const success = await ragAPI.removeDocument(id)

      if (success) {
        setDocuments(documents.filter(doc => doc.id !== id))
        toast({
          title: 'Documento removido',
          description: 'Documento foi removido com sucesso',
        })
        loadData()
      }
    } catch (error) {
      toast({
        title: 'Erro',
        description: 'Não foi possível remover o documento',
        variant: 'destructive'
      })
    }
  }

  // Componente de card de documento
  const DocumentCard = ({ doc }: { doc: RAGDocument }) => (
    <Card className="hover:shadow-lg transition-shadow">
      <CardHeader>
        <div className="flex justify-between items-start">
          <div className="flex-1">
            <CardTitle className="text-lg">{doc.title || 'Sem título'}</CardTitle>
            <CardDescription className="mt-1">
              <div className="flex items-center gap-2 text-xs">
                <Badge variant="outline" className="flex items-center gap-1">
                  <FolderOpen className="h-3 w-3" />
                  {doc.category || 'uncategorized'}
                </Badge>
                <Badge variant="outline" className="flex items-center gap-1">
                  <FileText className="h-3 w-3" />
                  {doc.type || 'text'}
                </Badge>
                {doc.score && (
                  <Badge variant="secondary" className="flex items-center gap-1">
                    <Brain className="h-3 w-3" />
                    {(doc.score * 100).toFixed(1)}%
                  </Badge>
                )}
              </div>
            </CardDescription>
          </div>
          <div className="flex gap-1">
            <Button
              size="icon"
              variant="ghost"
              onClick={() => setSelectedDoc(doc)}
            >
              <Edit className="h-4 w-4" />
            </Button>
            <Button
              size="icon"
              variant="ghost"
              onClick={() => handleRemoveDocument(doc.id)}
            >
              <Trash2 className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </CardHeader>
      <CardContent>
        <p className="text-sm text-muted-foreground line-clamp-3 mb-3">
          {doc.content}
        </p>
        {doc.tags && doc.tags.length > 0 && (
          <div className="flex flex-wrap gap-1">
            {doc.tags.map((tag, idx) => (
              <Badge
                key={idx}
                variant="secondary"
                className="text-xs cursor-pointer hover:bg-secondary/80"
                onClick={() => handleTagFilter(tag)}
              >
                <Tag className="h-3 w-3 mr-1" />
                {tag}
              </Badge>
            ))}
          </div>
        )}
        <div className="mt-3 text-xs text-muted-foreground">
          {doc.source && <span>Fonte: {doc.source}</span>}
          {doc.version && <span className="ml-2">v{doc.version}</span>}
          {doc.hash && (
            <span className="ml-2 flex items-center gap-1 inline">
              <Hash className="h-3 w-3" />
              {doc.hash.substring(0, 8)}
            </span>
          )}
        </div>
      </CardContent>
    </Card>
  )

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Estatísticas */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm font-medium">Total de Documentos</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.total_documents}</div>
              <p className="text-xs text-muted-foreground">
                {stats.unique_hashes} únicos
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm font-medium">Tamanho do Cache</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{stats.total_size_mb} MB</div>
              <p className="text-xs text-muted-foreground">
                {stats.total_size_bytes.toLocaleString()} bytes
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm font-medium">Embeddings</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {stats.has_embeddings ? (
                  <Badge variant="default" className="flex items-center gap-1">
                    <Brain className="h-3 w-3" />
                    Ativo
                  </Badge>
                ) : (
                  <Badge variant="secondary">Inativo</Badge>
                )}
              </div>
              {stats.embedding_model && (
                <p className="text-xs text-muted-foreground mt-1">
                  {stats.embedding_model}
                </p>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-3">
              <CardTitle className="text-sm font-medium">Categorias</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                {Object.keys(stats.categories || {}).length}
              </div>
              <p className="text-xs text-muted-foreground">
                {Object.keys(stats.sources || {}).length} fontes
              </p>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Barra de busca e filtros */}
      <Card>
        <CardContent className="pt-6">
          <div className="flex gap-2">
            <div className="flex-1 flex gap-2">
              <Input
                placeholder="Buscar documentos..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                className="flex-1"
              />
              <Select value={searchType} onValueChange={(v: 'semantic' | 'text') => setSearchType(v)}>
                <SelectTrigger className="w-32">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="semantic">
                    <div className="flex items-center gap-2">
                      <Brain className="h-4 w-4" />
                      Semântica
                    </div>
                  </SelectItem>
                  <SelectItem value="text">
                    <div className="flex items-center gap-2">
                      <FileText className="h-4 w-4" />
                      Texto
                    </div>
                  </SelectItem>
                </SelectContent>
              </Select>
              <Button onClick={handleSearch} variant="default">
                <Search className="h-4 w-4 mr-2" />
                Buscar
              </Button>
            </div>

            <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
              <DialogTrigger asChild>
                <Button variant="outline">
                  <Plus className="h-4 w-4 mr-2" />
                  Adicionar
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Adicionar Documento</DialogTitle>
                  <DialogDescription>
                    Adicione um novo documento ao cache RAG
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4">
                  <Input
                    placeholder="Título do documento"
                    value={newDoc.title}
                    onChange={(e) => setNewDoc({ ...newDoc, title: e.target.value })}
                  />
                  <Textarea
                    placeholder="Conteúdo do documento"
                    value={newDoc.content}
                    onChange={(e) => setNewDoc({ ...newDoc, content: e.target.value })}
                    rows={5}
                  />
                  <Input
                    placeholder="Tags (separadas por vírgula)"
                    value={newDoc.tags}
                    onChange={(e) => setNewDoc({ ...newDoc, tags: e.target.value })}
                  />
                  <Select
                    value={newDoc.category}
                    onValueChange={(v) => setNewDoc({ ...newDoc, category: v })}
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="uncategorized">Sem categoria</SelectItem>
                      <SelectItem value="documentation">Documentação</SelectItem>
                      <SelectItem value="code">Código</SelectItem>
                      <SelectItem value="webpage">Página Web</SelectItem>
                      <SelectItem value="text">Texto</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <DialogFooter>
                  <Button variant="outline" onClick={() => setIsAddDialogOpen(false)}>
                    Cancelar
                  </Button>
                  <Button onClick={handleAddDocument}>
                    Adicionar
                  </Button>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          </div>

          {/* Filtros por categoria */}
          {stats?.categories && (
            <div className="mt-4 flex gap-2 flex-wrap">
              <Badge
                variant={selectedCategory === 'all' ? 'default' : 'outline'}
                className="cursor-pointer"
                onClick={() => handleCategoryFilter('all')}
              >
                Todas
              </Badge>
              {Object.entries(stats.categories).map(([cat, count]) => (
                <Badge
                  key={cat}
                  variant={selectedCategory === cat ? 'default' : 'outline'}
                  className="cursor-pointer"
                  onClick={() => handleCategoryFilter(cat)}
                >
                  {cat} ({count})
                </Badge>
              ))}
            </div>
          )}

          {/* Tags populares */}
          {stats?.top_tags && Object.keys(stats.top_tags).length > 0 && (
            <div className="mt-4">
              <p className="text-xs text-muted-foreground mb-2">Tags populares:</p>
              <div className="flex gap-2 flex-wrap">
                {Object.entries(stats.top_tags).map(([tag, count]) => (
                  <Badge
                    key={tag}
                    variant={selectedTags.includes(tag) ? 'default' : 'secondary'}
                    className="cursor-pointer text-xs"
                    onClick={() => handleTagFilter(tag)}
                  >
                    <Tag className="h-3 w-3 mr-1" />
                    {tag} ({count})
                  </Badge>
                ))}
              </div>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Resultados */}
      <Tabs defaultValue="results" className="w-full">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="results">
            Resultados ({searchResults.length || documents.length})
          </TabsTrigger>
          <TabsTrigger value="all">
            Todos os Documentos ({documents.length})
          </TabsTrigger>
        </TabsList>

        <TabsContent value="results" className="mt-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {(searchResults.length > 0 ? searchResults : documents).map((doc) => (
              <DocumentCard key={doc.id} doc={doc} />
            ))}
          </div>
        </TabsContent>

        <TabsContent value="all" className="mt-4">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {documents.map((doc) => (
              <DocumentCard key={doc.id} doc={doc} />
            ))}
          </div>
        </TabsContent>
      </Tabs>

      {/* Dialog de detalhes do documento */}
      {selectedDoc && (
        <Dialog open={!!selectedDoc} onOpenChange={() => setSelectedDoc(null)}>
          <DialogContent className="max-w-3xl max-h-[80vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>{selectedDoc.title || 'Documento sem título'}</DialogTitle>
              <DialogDescription>
                ID: {selectedDoc.id} | Versão: {selectedDoc.version || 1}
              </DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div>
                <h4 className="font-medium mb-2">Conteúdo</h4>
                <pre className="bg-muted p-4 rounded text-sm whitespace-pre-wrap">
                  {selectedDoc.content}
                </pre>
              </div>

              {selectedDoc.metadata && (
                <div>
                  <h4 className="font-medium mb-2">Metadados</h4>
                  <pre className="bg-muted p-4 rounded text-sm">
                    {JSON.stringify(selectedDoc.metadata, null, 2)}
                  </pre>
                </div>
              )}

              <div className="flex gap-4 text-sm">
                <div>
                  <span className="font-medium">Criado em:</span>{' '}
                  {new Date(selectedDoc.created_at || '').toLocaleString()}
                </div>
                <div>
                  <span className="font-medium">Atualizado em:</span>{' '}
                  {new Date(selectedDoc.updated_at || '').toLocaleString()}
                </div>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  )
}

