#!/usr/bin/env node
import { config } from "dotenv";
import { Server } from "@modelcontextprotocol/sdk/server/index.js";
import { StdioServerTransport } from "@modelcontextprotocol/sdk/server/stdio.js";
import { createClient } from "@libsql/client";
import {
  CallToolRequestSchema,
  ListToolsRequestSchema,
} from "@modelcontextprotocol/sdk/types.js";

// Load environment variables
config();

// Turso client
let tursoClient: any = null;

function getTursoClient() {
  if (!tursoClient) {
    const databaseUrl = process.env.TURSO_DATABASE_URL;
    const authToken = process.env.TURSO_AUTH_TOKEN;

    if (!databaseUrl || !authToken) {
      throw new Error("TURSO_DATABASE_URL e TURSO_AUTH_TOKEN s√£o obrigat√≥rios");
    }

    tursoClient = createClient({
      url: databaseUrl,
      authToken: authToken,
    });
  }
  return tursoClient;
}

// Validation helper
function validateRequiredParams(args: any, requiredParams: string[]): void {
  for (const param of requiredParams) {
    if (!args?.[param]) {
      throw new Error(`Par√¢metro obrigat√≥rio '${param}' n√£o fornecido`);
    }
  }
}

// Create server instance
const server = new Server(
  {
    name: "mcp-turso-memory",
    version: "1.0.0",
  }
);

// Tool handlers
server.setRequestHandler(ListToolsRequestSchema, async () => {
  return {
    tools: [
      {
        name: "turso_list_databases",
        description: "List all databases in your Turso organization",
        inputSchema: {
          type: "object",
          properties: {
            random_string: {
              type: "string",
              description: "Dummy parameter for no-parameter tools",
            },
          },
          required: ["random_string"],
        },
      },
      {
        name: "turso_execute_query",
        description: "Execute a SQL query on the Turso database",
        inputSchema: {
          type: "object",
