#!/usr/bin/env python3
"""
Servidor MCP Graphiti-Turso usando FastMCP
Vers√£o simplificada e funcional
"""

import sys
import json
from typing import Any, Dict, List, Optional
from mcp.server import FastMCP

# Criar servidor
mcp = FastMCP("graphiti-turso")

# Armazenamento em mem√≥ria
memory_store = {
    "episodes": [],
    "embeddings": {},
    "searches": []
}

@mcp.tool()
async def add_episode(name: str, content: str, metadata: Optional[Dict] = None) -> Dict[str, Any]:
    """Adiciona um epis√≥dio ao sistema de mem√≥ria Graphiti"""
    episode = {
        "id": f"ep_{len(memory_store['episodes'])}",
        "name": name,
        "content": content,
        "metadata": metadata or {},
        "timestamp": str(len(memory_store['episodes']))
    }
    memory_store["episodes"].append(episode)

    return {
        "status": "success",
        "episode_id": episode["id"],
        "message": f"Epis√≥dio '{name}' adicionado com sucesso"
    }

@mcp.tool()
async def search_knowledge(query: str, limit: int = 5) -> List[Dict[str, Any]]:
    """Busca conhecimento relevante no Graphiti"""
    results = []
    query_lower = query.lower()

    for episode in memory_store["episodes"]:
        if query_lower in episode["content"].lower() or query_lower in episode["name"].lower():
            results.append({
                "id": episode["id"],
                "name": episode["name"],
                "content": episode["content"][:200] + "..." if len(episode["content"]) > 200 else episode["content"],
                "score": 0.8
            })

    return results[:limit]

@mcp.tool()
async def list_episodes(limit: int = 10) -> List[Dict[str, Any]]:
    """Lista os epis√≥dios mais recentes"""
    episodes = memory_store["episodes"][-limit:]
    return [
        {
            "id": ep["id"],
            "name": ep["name"],
            "preview": ep["content"][:100] + "..." if len(ep["content"]) > 100 else ep["content"]
        }
        for ep in episodes
    ]

@mcp.tool()
async def get_status() -> Dict[str, Any]:
    """Retorna status do sistema Graphiti-Turso"""
    return {
        "server": "Graphiti-Turso MCP",
        "version": "1.0.0-fastmcp",
        "total_episodes": len(memory_store["episodes"]),
        "total_searches": len(memory_store["searches"]),
        "backend": "Memory (Development Mode)",
        "status": "operational"
    }

@mcp.tool()
async def clear_memory() -> Dict[str, Any]:
    """Limpa toda a mem√≥ria do sistema"""
    memory_store["episodes"].clear()
    memory_store["embeddings"].clear()
    memory_store["searches"].clear()

    return {
        "status": "success",
        "message": "Mem√≥ria limpa com sucesso"
    }

@mcp.tool()
async def remove_episode(episode_id: str) -> Dict[str, Any]:
    """Remove um epis√≥dio espec√≠fico da mem√≥ria pelo ID"""
    for i, episode in enumerate(memory_store["episodes"]):
        if episode["id"] == episode_id:
            removed = memory_store["episodes"].pop(i)
            return {
                "status": "success",
                "message": f"Epis√≥dio {episode_id} removido com sucesso",
                "removed_episode": {
                    "id": removed["id"],
                    "name": removed["name"]
                }
            }

    return {
        "status": "error",
        "message": f"Epis√≥dio {episode_id} n√£o encontrado"
    }

@mcp.tool()
async def remove_episodes_by_name(name_pattern: str) -> Dict[str, Any]:
    """Remove todos os epis√≥dios que contenham o padr√£o no nome"""
    removed_episodes = []
    name_lower = name_pattern.lower()

    # Criar nova lista sem os epis√≥dios que correspondem ao padr√£o
    new_episodes = []
    for episode in memory_store["episodes"]:
        if name_lower in episode["name"].lower():
            removed_episodes.append({
                "id": episode["id"],
                "name": episode["name"]
            })
        else:
            new_episodes.append(episode)

    memory_store["episodes"] = new_episodes

    if removed_episodes:
        return {
            "status": "success",
            "message": f"Removidos {len(removed_episodes)} epis√≥dios",
            "removed_episodes": removed_episodes
        }
    else:
        return {
            "status": "info",
            "message": f"Nenhum epis√≥dio encontrado com o padr√£o '{name_pattern}'"
        }

@mcp.tool()
async def update_episode(episode_id: str, name: Optional[str] = None, content: Optional[str] = None, metadata: Optional[Dict] = None) -> Dict[str, Any]:
    """Atualiza um epis√≥dio existente"""
    for episode in memory_store["episodes"]:
        if episode["id"] == episode_id:
            # Atualizar apenas os campos fornecidos
            if name is not None:
                episode["name"] = name
            if content is not None:
                episode["content"] = content
            if metadata is not None:
                episode["metadata"] = metadata

            return {
                "status": "success",
                "message": f"Epis√≥dio {episode_id} atualizado com sucesso",
                "updated_episode": {
                    "id": episode["id"],
                    "name": episode["name"],
                    "content_preview": episode["content"][:100] + "..." if len(episode["content"]) > 100 else episode["content"]
                }
            }

    return {
        "status": "error",
        "message": f"Epis√≥dio {episode_id} n√£o encontrado"
    }

@mcp.tool()
async def get_episode(episode_id: str) -> Dict[str, Any]:
    """Obt√©m um epis√≥dio espec√≠fico pelo ID"""
    for episode in memory_store["episodes"]:
        if episode["id"] == episode_id:
            return {
                "status": "success",
                "episode": episode
            }

    return {
        "status": "error",
        "message": f"Epis√≥dio {episode_id} n√£o encontrado"
    }

if __name__ == "__main__":
    try:
        # Executar servidor em modo stdio (padr√£o para Claude)
        mcp.run()
    except KeyboardInterrupt:
        print("\nServidor encerrado", file=sys.stderr)
    except Exception as e:
        print(f"Erro: {e}", file=sys.stderr)
        sys.exit(1)

